function containsInlineMedia(htmlContent) {
  // Create a temporary DOM container to parse the HTML
  const container = document.createElement('div');
  container.innerHTML = htmlContent;

  // Check for inline images, videos, and embeds
  const hasMedia = 
    container.querySelector('img') !== null || // Detect images (including GIFs)
    container.querySelector('video') !== null || // Detect videos
    container.querySelector('audio') !== null || // Detect audio files
    container.querySelector('iframe') !== null || // Detect embedded content
    container.querySelector('embed') !== null || // Detect embedded files
    container.querySelector('object') !== null; // Detect embedded objects

  return hasMedia;
}


return containsInlineMedia(richText); // true if there's inline media

