with big_ups_where_this_person_is_recipient as (
  select distinct(big_ups_id) 
    from big_ups_recipients
  where recipient_id = {{ getPerson.data.id }}
)

select
  date_trunc('day', bu.created_at),
  bu.details,
  giver.full_name_preferred as "giver",
  bu.id,
  array_agg(re.full_name_preferred) recipients
  -- array_agg(id) ids
from
  big_ups bu
  left join rippling_employees giver on giver.id = bu.giver_id
  left join big_ups_recipients bur on bur.big_ups_id = bu.id
  left join rippling_employees re on re.id = bur.recipient_id
where bu.id in (select * from big_ups_where_this_person_is_recipient)
group by
  1,2,3,4
