              WHEN 'gsheet' THEN 2
              WHEN 'gpres' THEN 3
              WHEN 'pdf' THEN 4
              WHEN 'gif' THEN 5
              WHEN 'png' THEN 5
              WHEN 'image/png' THEN 5
              WHEN 'image/webp' THEN 5
              WHEN 'jpg' THEN 5
              WHEN 'webm' THEN 6
