select 
  d.*,
  re.full_name_preferred decision_maker_preferred_name,
  re.photo_url_small decision_maker_photo_url,
  r.recommender_preferred_name,
  array_agg(t.name)
from decision_log d
left join decision_teams_affected dt on d.id = dt.decision_id
left join teams t on t.id = dt.team_id
left join rippling_employees re on re.id = d.decision_maker
left join recommender r on d.recommender = r.id
where
  d.is_archived = false
  and d.is_test = false
  and is_public = true
  and (
    ({{ _.isEmpty(searchDecisions.value) }} or d.decision ilike {{ '%' + searchDecisions.value + '%' }}) 
    or ({{ _.isEmpty(searchDecisions.value) }} or d.decision_to_make ilike {{ '%' + searchDecisions.value + '%' }})
  )
  and (
    {{ _.isEmpty(searchDecisionsStatus.value) }} or d.decision_status = {{ searchDecisionsStatus.value }}
  )
group by 
  d.id, 
  decision_maker_preferred_name,
  decision_maker_photo_url,
  r.recommender_preferred_name
order by d.created_at desc
