}

._retool-shippedImageAttachmentContainer:hover {
  /* box-shadow: 0px 2px 8px 0px rgba(0,0,0,0.1); */
}

/* rich text edtor border */

div._retool-richTextEditor2.block-component, 
div._retool-richTextEditor1.block-component {
  border-color: {{ theme.surfaceSecondaryBorder }} !important;
  overflow: auto;
}

div.ql-editor {
  background-color: {{ theme.surfaceSecondary }}
}

/* styling for required field in form */

.red-asterisk {
  color: {{ theme.danger }};
  margin-left: 2px;
}

/* user settings modal - position & style */

[data-testid="ViewerPageLayout::modalFrameSettings"] {
  position: fixed !important;
  right: 20px !important;
  top: 50px !important;
  border: 1px solid {{ theme.tokens.border50 }};
  border-radius: 4px;
    /* width: 200px !important; */
}

/* user settings modal - width */

[data-testid="ViewerPageLayout::modalFrameSettings"] [data-testid="FrameStyleSection::modal"] {
  max-width: 140px;
}

/* user avatar hover */
[data-testid="RetoolStackWidget::avatar1"] {
  border-radius: 4px;
  transition: .15s ease-in-out;
  cursor: pointer;
}

[data-testid="RetoolStackWidget::avatar1"] * {
  cursor: pointer;
}

[data-testid="RetoolStackWidget::avatar1"]:hover {
  background-color: rgba(0, 0, 0, 0.07);

}

/*  profile - percentile css hover */
/* Tooltip container */
.tooltip {
  display: inline-block;
}

/* Tooltip text */
.tooltip .tooltiptext {
  visibility: hidden;
  width: 220px;
  background-color: {{theme.tokens.canvasHover}};
  color: {{ theme.primary }};
  border: 1px solid {{ theme.tokens.border755 }};
  text-align: center;
  /* padding: 5px 0; */
  border-radius: 4px;
  padding: 4px;

  /* Position the tooltip text - see examples below! */
  position: absolute;
  left: -70px;
  bottom: 25px;
  z-index: 999999;
  font-size: 12px;
  line-height: 1.4em;
  text-align: center;
}
.tooltip p {
  line-height: 1em !important;
  border-bottom: 1px dashed {{ theme.tokens.border50 }};
}

/* Show the tooltip text when you mouse over the tooltip container */
.tooltip:hover .tooltiptext {
  visibility: visible;

}

/* create badge form */
.secondary-text {
  font-size: .9em;
  color: {{ theme.tokens.secondaryText }};
  font-weight: 400;

}

/* ship form RTE */
._retool-richTextEditor1 .ql-editor p {
  font-size: 1.2em;
  margin-bottom: .7em;
  line-height: 1.4em
}

._retool-richTextEditor1 .ql-editor li {
  font-size: 1.2em;
  line-height: 1.4em
}

._retool-richTextEditor1 .ql-editor ol, ._retool-richTextEditor1 .ql-editor ul {
  margin-bottom: 10px;
