      sum(bu_arr) as sum_arr,
      'committed' as customer_type
    from
      dbt_entities.business_units_summary
    where
      bu_status = 'Active'
      and is_paying_customer = true
  ),
  committed2_arr as (
    SELECT
      count(distinct opportunity.business_unit__c) as count_customers,
      sum(opportunity.new_annualized_revenue__c) as sum_arr,
      'committed2' as customer_type
    FROM
      production.polytomic_salesforce.opportunity as opportunity
    WHERE
      opportunity.Is_Committed_Booking__c = TRUE
  ),
  committed2_count as (
