    c.month, 
    'total' as motion_type,
    (c.count_customers + ss.count_customers) as count_customers, 
    (c.arr + ss.arr) as arr,
    case
      when c.month = current_date() then true 
      else false
    end as is_today
  from committed c
  left join self_serve ss on c.month = ss.month
)

