  select
    *,
    lag(eop_arr) over (order by datestamp) as bop_arr
  from (
    select
      datestamp,
      sum(mrr_excluding_startup_credits_usd)*12 as eop_arr,
      sum(
      case 
        when mrr_excluding_startup_credits_usd > 0 then 1
        else 0
      end
    ) as count_customers
    from 
      production.dbt_mart.rpt_selfserve_revenue
    where 
      is_month_end
      and customer_credits_change_type_month not in ('Moved to Committed','Previously Moved to Committed')
    group by 1 order by 1 desc
  )
  order by 1 desc
), 
committed_arr_base as (
  SELECT
    trunc(booking_date__c, 'month') as booking_month,
    sum(sum(opportunity.new_annualized_revenue__c)) over (order by trunc(booking_date__c,'month')) as arr
  FROM 
    production.polytomic_salesforce.opportunity as opportunity
  WHERE 
    opportunity.Is_Committed_Booking__c = TRUE
    and booking_date__c <= current_date()
  GROUP BY 
    trunc(booking_date__c, 'month')
),
committed_count AS (
SELECT 
    date_trunc('month',datestamp) AS booking_month, 
    COUNT(*) AS count_customers 
  FROM 
    production.dbt_mart.rpt_salesforce_revenue 
  WHERE 
    datestamp = (SELECT MAX(datestamp) FROM production.dbt_mart.rpt_salesforce_revenue)
    OR datestamp = LAST_DAY(ADD_MONTHS((SELECT MAX(datestamp) FROM production.dbt_mart.rpt_salesforce_revenue), -1))
  GROUP BY 
    datestamp
),
ss_arr as (
  select 
    'self-serve' as motion_type,
    count_customers,
    date_trunc('month',ss.datestamp) as date,
    case 
      when date_trunc('month',ss.datestamp) = date_trunc('month', current_date()) then true 
      else false
    end as is_today,
    ss.eop_arr as arr
  from ss_arr_base ss
  where ss.datestamp between add_months(date_trunc('month', current_date()),-1) and current_date()
  order by ss.datestamp asc
),

com_arr as (
  select
    'committed' as motion_type, 
    cc.count_customers as count_customers,
    date_trunc('month',b.booking_month) as date,
    case 
      when b.booking_month = date_trunc('month', current_date()) then true 
      else false
    end as is_today,
    b.arr
  from committed_arr_base as b
  left join committed_count cc on cc.booking_month = b.booking_month
  where b.booking_month between add_months(date_trunc('month', current_date()),-1) and current_date()
  order by b.booking_month asc
),
total_arr as (
  select 
    'total' as motion_type,
    (c.count_customers + s.count_customers) as count_customers,
    c.date,
    c.is_today,
    (c.arr + s.arr) as arr
  from
    com_arr c 
  left join ss_arr s on c.date = s.date
)

select * from total_arr
union all
select * from ss_arr 
union all
select * from com_arr


